def encrypter(string, special_chars_dict,x):
    encripted_msg = ''
    for i in string:
        encripted_msg += special_chars_dict[i]
    return add_Ecode(encripted_msg,x)

def add_Ecode(string,x):
    string = string[0:len(string)//2] + str(x) + string[len(string)//2:]
    return string

